import React, { useState, useEffect, useRef } from 'react';
import { Input, Button } from '@/components/ui/input';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { PCA } from 'ml-pca';

const EmbeddingVisualizer = () => {
  const [strings, setStrings] = useState('');
  const [apiKey, setApiKey] = useState('');
  const [embeddings, setEmbeddings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const canvasRef = useRef(null);

  const getEmbeddings = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch('https://api.openai.com/v1/embeddings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          input: strings.split('\n'),
          model: 'text-embedding-ada-002'
        })
      });

      if (!response.ok) {
        throw new Error('Failed to fetch embeddings');
      }

      const data = await response.json();
      setEmbeddings(data.data.map(item => item.embedding));
    } catch (err) {
      setError('Error fetching embeddings: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (embeddings.length > 0 && canvasRef.current) {
      const scene = new THREE.Scene();
      const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
      const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current });
      renderer.setSize(window.innerWidth, window.innerHeight);

      const controls = new OrbitControls(camera, renderer.domElement);

      // Perform PCA to reduce dimensionality to 3D
      const pca = new PCA(embeddings);
      const reducedData = pca.predict(embeddings, { nComponents: 3 });

      // Create points
      const geometry = new THREE.BufferGeometry();
      const positions = new Float32Array(reducedData.getRows().flat());
      geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

      const material = new THREE.PointsMaterial({ color: 0x00ff00, size: 0.1 });
      const points = new THREE.Points(geometry, material);
      scene.add(points);

      camera.position.z = 5;

      const animate = () => {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
      };

      animate();

      return () => {
        renderer.dispose();
      };
    }
  }, [embeddings]);

  return (
    <div className="p-4">
      <div className="mb-4">
        <Input
          value={strings}
          onChange={(e) => setStrings(e.target.value)}
          placeholder="Enter strings (one per line)"
          className="mb-2"
        />
        <Input
          type="password"
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          placeholder="Enter OpenAI API Key"
          className="mb-2"
        />
        <Button onClick={getEmbeddings} disabled={loading}>
          {loading ? 'Loading...' : 'Generate Embeddings'}
        </Button>
      </div>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <canvas ref={canvasRef} />
    </div>
  );
};

export default EmbeddingVisualizer;
